
var groundskeeper=require("groundskeeper");module.exports=function(a){a.registerMultiTask("remove-logging","remove console.log for distribution files",function(){this.files.forEach(function(b){b.src.filter(function(c){if(!a.file.exists(c)){a.log.warn('Source file "'+c+'" not found.');return false}else{return true}}).map(function(e){var d=a.file.read(e);var c=groundskeeper();c.write(d);a.file.write(b.dest,c.toString())})})})};

 
